<style scoped>
  .help {
    display: inline-block;
  }
  .help.dark:hover::after {
    position: absolute;
    content: attr(data-title); /* Выводим текст */
    background-color: #313335;
    color: #ffffff;
    padding: 10px;
    top: 8px;
  }
  .help.white:hover::after {
    position: absolute;
    content: attr(data-title); /* Выводим текст */
    background-color: #f0f0f0;
    color: #313335;
    padding: 10px;
    top: 8px;
  }

  .btn {
    -webkit-appearance: none;
    font-size: 1rem;
    text-shadow: none;
    line-height: 1.2;
    display: inline-block;
    margin: 0 0 5px 0;
    position: relative;
    border-radius: 4px;
    cursor: pointer;
    white-space: nowrap;
    text-overflow: ellipsis;
    text-decoration: none !important;
    text-align: center;
    font-weight: normal !important;
  }
  .version-selector {
    padding :3px 6px;
  }
  .icon {
    margin-left: 5px;
  }

  .is-hidden {
    display: none;
  }

  .is-small {
    padding: 3px 10px;
  }
  .is-blue {
    background: none;
    border: 2px solid #007cd7;
    min-width: 40px;
  }
  .white {
    color: black;
  }
  .black {
    color: white;
  }
  .white-dropdown {
    color: black;
  }
  .black-dropdown {
    color: white;
  }
  .white-dropdown option {
    color: white;
  }
  .black-dropdown option {
    color: black;
  }
  .is-blue:hover svg {
    color: #007cd7;
  }

  .header {
    display: flex;
    width: 100%;
    max-width: 1200px;
    justify-content: space-between;
  }

  .header-element {
    text-align: center;
    margin: 0 10px;
  }

  .header-element:first-child {
    width: 40%;
    margin-left: 0px;
    text-align: left;
