<script>
import { eventBus } from '../../main.js'
export default {
  name: 'HeaderBasic',
  watch: {
    nightMode: function () {
      this.$store.dispatch('user/saveNightMode', this.nightMode)
    },
    version: function () {
      this.$store.dispatch('user/saveVersion', this.version)
    }
  },
  data: function () {
    return {
      eventBus: eventBus,
      nightMode: this.$store.getters['user/getNightMode'] || false,
      version: this.$store.getters['user/getVersion'] || 7.3
    }
  },
  computed: {
    themeButton: function () {
      if (this.$store.getters['user/getNightMode']) {
        return 'black'
      } else {
        return 'white'
      }
    },
    themeSelect: function () {
      if (this.$store.getters['user/getNightMode']) {
        return 'black-dropdown'
      } else {
        return 'white-dropdown'
      }
    },
    themeHelp: function () {
      if (this.$store.getters['user/getNightMode']) {
        return 'dark'
      } else {
        return 'white'
      }
    }
  },
  methods: {
    runCode: function () {
      eventBus.$emit('runCode')
    },
    toggle: function () {
      this.nightMode = !this.nightMode
    }
  }
}
</script>
